import React, { useState } from "react";
import axios from "axios";

export default function ReviewForm({ onSubmit, editMode, initialForm, token }) {
  const [form, setForm] = useState(
    initialForm || {
      bookTitle: "",
      author: "",
      review: "",
      rating: "",
      coverImage: "",
    }
  );
  const [coverFile, setCoverFile] = useState(null);
  const [coverPreview, setCoverPreview] = useState("");

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleCoverChange = (e) => {
    const file = e.target.files[0];
    setCoverFile(file);
    if (file) {
      setCoverPreview(URL.createObjectURL(file));
    } else {
      setCoverPreview("");
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    let coverImageUrl = form.coverImage;
    if (coverFile) {
      const data = new FormData();
      data.append("cover", coverFile);
      const res = await axios.post("/api/upload-cover", data);
      coverImageUrl = res.data.url;
    }
    onSubmit({ ...form, coverImage: coverImageUrl });
    setForm({
      bookTitle: "",
      author: "",
      review: "",
      rating: "",
      coverImage: "",
    });
    setCoverFile(null);
    setCoverPreview("");
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        name="bookTitle"
        value={form.bookTitle}
        onChange={handleChange}
        placeholder="Book Title"
        required
      />
      <input
        type="text"
        name="author"
        value={form.author}
        onChange={handleChange}
        placeholder="Author"
        required
      />
      <textarea
        name="review"
        value={form.review}
        onChange={handleChange}
        placeholder="Your Review"
        required
      />
      <input
        type="number"
        name="rating"
        value={form.rating}
        onChange={handleChange}
        placeholder="Rating (1-5)"
        min="1"
        max="5"
        required
      />
      <input
        type="file"
        accept="image/*"
        onChange={handleCoverChange}
        style={{ marginBottom: 12 }}
      />
      {coverPreview && (
        <img
          src={coverPreview}
          alt="Cover Preview"
          style={{ maxWidth: 120, marginBottom: 12, borderRadius: 8 }}
        />
      )}
      <button type="submit" className="add-review-btn">
        {editMode ? "Update Review" : "Add Review"}
      </button>
    </form>
  );
}
