import React, { useState } from "react";
import ReviewCard from "./ReviewCard";

const ProfilePage = ({
  profile,
  onSaveProfile,
  onUploadAvatar,
  onChangePassword,
  profileReviews,
  onEdit,
  onDelete,
  onFavorite,
}) => {
  const [form, setForm] = useState({
    username: profile?.username || "",
    bio: profile?.bio || "",
  });
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState("");
  const [pwdForm, setPwdForm] = useState({
    currentPassword: "",
    newPassword: "",
    confirmPassword: "",
  });
  const [pwdError, setPwdError] = useState("");
  const [pwdSaving, setPwdSaving] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handlePwdChange = (e) => {
    setPwdForm({ ...pwdForm, [e.target.name]: e.target.value });
  };

  const handlePwdSave = async (e) => {
    e.preventDefault();
    setPwdError("");
    if (pwdForm.newPassword.length < 8) {
      setPwdError("New password must be at least 8 characters.");
      return;
    }
    if (pwdForm.newPassword !== pwdForm.confirmPassword) {
      setPwdError("Passwords do not match.");
      return;
    }
    setPwdSaving(true);
    try {
      await onChangePassword({
        currentPassword: pwdForm.currentPassword,
        newPassword: pwdForm.newPassword,
      });
      setPwdForm({ currentPassword: "", newPassword: "", confirmPassword: "" });
      alert("Password updated successfully");
    } catch (err) {
      setPwdError(err?.response?.data?.message || "Failed to update password.");
    } finally {
      setPwdSaving(false);
    }
  };

  const handleSave = async (e) => {
    e.preventDefault();
    setError("");
    setSaving(true);
    try {
      await onSaveProfile(form);
    } catch (err) {
      setError(err?.response?.data?.message || "Failed to update profile.");
    } finally {
      setSaving(false);
    }
  };

  return (
    <div>
      <h2>Your Profile</h2>
      <div style={{ display: "flex", gap: 16, alignItems: "center" }}>
        <img
          src={profile?.avatarUrl || "https://via.placeholder.com/96"}
          alt="Avatar"
          style={{
            width: 96,
            height: 96,
            borderRadius: "50%",
            objectFit: "cover",
          }}
        />
        <div>
          <input type="file" accept="image/*" onChange={onUploadAvatar} />
          <div style={{ fontSize: 12, color: "#6b7280" }}>
            Recommended: square image
          </div>
        </div>
      </div>

      <form onSubmit={handleSave} style={{ marginTop: 16 }}>
        <input
          type="text"
          name="username"
          value={form.username}
          onChange={handleChange}
          placeholder="Username"
          required
        />
        <textarea
          name="bio"
          value={form.bio}
          onChange={handleChange}
          placeholder="Bio"
          rows={3}
        />
        {error && <p style={{ color: "red" }}>{error}</p>}
        <button type="submit" disabled={saving}>
          {saving ? "Saving..." : "Save Profile"}
        </button>
      </form>

      <h3 style={{ marginTop: 24 }}>Change Password</h3>
      <form onSubmit={handlePwdSave}>
        <input
          type="password"
          name="currentPassword"
          value={pwdForm.currentPassword}
          onChange={handlePwdChange}
          placeholder="Current password"
          required
        />
        <input
          type="password"
          name="newPassword"
          value={pwdForm.newPassword}
          onChange={handlePwdChange}
          placeholder="New password (min 8 chars)"
          required
        />
        <input
          type="password"
          name="confirmPassword"
          value={pwdForm.confirmPassword}
          onChange={handlePwdChange}
          placeholder="Confirm new password"
          required
        />
        {pwdError && <p style={{ color: "red" }}>{pwdError}</p>}
        <button type="submit" disabled={pwdSaving}>
          {pwdSaving ? "Updating..." : "Update Password"}
        </button>
      </form>

      <h2 style={{ marginTop: 24 }}>Your Reviews & Favorites</h2>
      <div className="reviews">
        {profileReviews.length === 0 ? (
          <p>No reviews yet.</p>
        ) : (
          profileReviews.map((review) => (
            <ReviewCard
              key={review._id}
              review={review}
              onEdit={onEdit}
              onDelete={onDelete}
              onFavorite={onFavorite}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default ProfilePage;
